import { verify } from "jsonwebtoken";

const getUserID = (token: string) => {
  try {
    const payload: any = verify(token, process.env.JWT_SECRET);
    console.log(payload);

    return payload.sub;
  } catch {
    return null;
  }
};
export default getUserID;
